svg.script:
var KEY = { w:87, a:65, s:83, d:68 };
var circle = document.getElementById("mouseblob");
var mask = document.getElementById("mousemask");

var svg = document.querySelector('svg');

// Create an SVGPoint for future math
var pt = svg.createSVGPoint();

// Get point in global SVG space
function cursorPoint(evt){
  pt.x = evt.clientX; pt.y = evt.clientY;
  return pt.matrixTransform(svg.getScreenCTM().inverse());
}

svg.addEventListener('mousemove',(evt) => {
  var loc = cursorPoint(evt);
  // Use loc.x and loc.y here
  circle.setAttribute('cx',loc.x);
  circle.setAttribute('cy',loc.y);
  mask.setAttribute('cx',loc.x);
  mask.setAttribute('cy',loc.y);
},false);

svg.addEventListener('mouseout', (event) => {
  circle.setAttribute('cx', 1000);
  circle.setAttribute('cy', 1000);
  mask.setAttribute('cx', 1000);
  mask.setAttribute('cy', 1000);
})
